<script type="text/javascript" language="javascript">function search_foc(){if(E('search_input').value=='<?=$search_txt?>'){E('search_input').value='';}E('search_input').style.color='black';if(E('search_input').style.width!='190px') $("#search_input").animate({"width": "190px"},{queue:false,duration:200});}function search_blur(){if(E('search_input').value==''){E('search_input').value='<?=$search_txt?>';E('search_input').style.color='#999999';$("#search_input").animate({"width": "170px"},{queue:false,duration:200});}}function doSearch(){if(E('search_input').value!=''&&E('search_input').value!='<?=$search_txt?>'){E('srcform').submit();}}</script><form id="srcform" action="<?=$search_action?>" method="get" style="margin:0;padding:0"><table cellspacing="0" cellpadding="0"><tr><td align="right"><input type="text" id="search_input" name="q" value="<?=$search_txt?>" onfocus="search_foc()" onblur="search_blur()" /></td><td width="24px" align="right"><input type="button" id="srcbtn" title="Search" onclick="doSearch()"/></td></tr></table></form>